var count = new GlideAggregate('incident');
count.addAggregate('COUNT');
count.query();
var incidents = 0;
if(count.next()){ 
   incidents = count.getAggregate('COUNT');
}

gs.info(incidents);


var target = new GlideRecord('incident'); 
target.addNotNullQuery('short_description');
target.query();   // Issue the query to the database to get all records where short_description is not null
while (target.next()) {   
     // add code here to process the incident record
gs.info(target.short_description);
}

var target = new GlideRecord('incident'); 
target.addNotNullQuery('problem_id');
target.setlimit(5);
target.setWorkflow(false);
target.orderBy('number');
target.query();   // Issue the query to the database to get all records where short_description is not null
while (target.next()) {   
     // add code here to process the incident record
gs.info('Number is ' + target.number + ' and problem number is ' + target.problem_id);
}
